#!/bin/bash

for i in `seq $1 $2`
do
   peer chaincode invoke -o orderer.example.com:7050 --tls true --cafile /opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/ordererOrganizations/example.com/orderers/orderer.example.com/msp/tlscacerts/tlsca.example.com-cert.pem -C mychannel -n mycc_$3 --peerAddresses peer0.org1.example.com:7051 --tlsRootCertFiles /opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/org1.example.com/peers/peer0.org1.example.com/tls/ca.crt --peerAddresses peer0.org2.example.com:9051 --tlsRootCertFiles /opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/org2.example.com/peers/peer0.org2.example.com/tls/ca.crt -c '{"Args":["Set","b",""]}' --waitForEvent 
   echo "Welcome $i times for b init = $1 end = $2 chaincodeID = $3  "
   res=1
   while [ $res -gt 0 ]
   do
     peer chaincode query -C mychannel -n mycc_$3 -c '{"Args":["query","b"]}'
     res=$?
   done
done
